@prefix rdfs:   <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd:    <http://www.w3.org/2001/XMLSchema#> .
@prefix ice:    <https://w3id.org/ice#> .
@prefix en15251: <https://webshop.ds.dk/da-dk/standard/ds-en-152512007#> .

# Pollution specifications
en15251:VeryLowPollutingBuilding
    a               en15251:PollutionSpecification ;
    rdfs:label      "Very low polluting building"@en ,
                    "Meget lavt forurenende bygning"@da ;
    ice:pollutionAirDemandMapping 
    [   ice:hasIndoorClimateClass en15251:IndoorClimateClassA ;
        ice:airDemandPerFloorArea "0.5"^^xsd:decimal ] ,
    [   ice:hasIndoorClimateClass en15251:IndoorClimateClassB ;
        ice:airDemandPerFloorArea "0.35"^^xsd:decimal ] ,
    [   ice:hasIndoorClimateClass en15251:IndoorClimateClassC ;
        ice:airDemandPerFloorArea "0.3"^^xsd:decimal ] .

en15251:LowPollutingBuilding
    a               en15251:PollutionSpecification ;
    rdfs:label      "Low polluting building"@en ,
                    "Lavt forurenende bygning"@da ;
    ice:pollutionAirDemandMapping 
    [   ice:hasIndoorClimateClass en15251:IndoorClimateClassA ;
        ice:airDemandPerFloorArea "1.0"^^xsd:decimal ] ,
    [   ice:hasIndoorClimateClass en15251:IndoorClimateClassB ;
        ice:airDemandPerFloorArea "0.7"^^xsd:decimal ] ,
    [   ice:hasIndoorClimateClass en15251:IndoorClimateClassC ;
        ice:airDemandPerFloorArea "0.4"^^xsd:decimal ] .

en15251:NonLowPollutingBuilding
    a               en15251:PollutionSpecification ;
    rdfs:label      "Non low-polluting building"@en ,
                    "Ikke lavtforurenende bygning"@da ;
    ice:pollutionAirDemandMapping 
    [   ice:hasIndoorClimateClass en15251:IndoorClimateClassA ;
        ice:airDemandPerFloorArea "2.0"^^xsd:decimal ] ,
    [   ice:hasIndoorClimateClass en15251:IndoorClimateClassB ;
        ice:airDemandPerFloorArea "1.4"^^xsd:decimal ] ,
    [   ice:hasIndoorClimateClass en15251:IndoorClimateClassC ;
        ice:airDemandPerFloorArea "0.8"^^xsd:decimal ] .

tabs.query:
PREFIX rdfs:    <http://www.w3.org/2000/01/rdf-schema#>
PREFIX ice:     <https://w3id.org/ice#>
PREFIX en15251: <https://webshop.ds.dk/da-dk/standard/ds-en-152512007#>

# RETURN AIR DEMAND FOR EACH POLLUTION CLASS
SELECT ?pollutionSpec ?airDemand 
WHERE { 
  ?pollutionSpec ice:pollutionAirDemandMapping ?map .
  ?map ice:hasIndoorClimateClass en15251:IndoorClimateClassB ;
       ice:airDemandPerFloorArea ?airDemand .
}

